INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (1,'Privredno pravo i korporativno upravljanje','Company Law and Corporate governance','<ul>\r\n<li>Savetovanje malih i srednjih privrednih dru&scaron;tava u re&scaron;avanju potencijalnih problema u poslovanju i otklanjanju istih</li>\r\n<li>Savetovanje korporacija i koncerna u vezi sa poslovanjem, korporativnim upravljanjem i drugim pratećim aktivnostima</li>\r\n<li>Izrada svih vrsta ugovora bitnih za poslovanje kompanije</li>\r\n<li>Praćenje zakonskih propisa i predlaganje zakonskih re&scaron;enja shodno potrebama klijenata, kao i celokupnog poslovnog okruženja</li>\r\n<li>Zajednička ulaganja, restrukturiranje, statusne promene</li>\r\n<li>Izrada due diligence izve&scaron;taja</li>\r\n<li>Priprema i savetovanje u vezi sa održavanjem skup&scaron;tina akcionara, sastanaka upravnih, nadzornih odbora i drugih organa privrednih dru&scaron;tava</li>\r\n<li>Savetovanje u oblasti privatizacije privrednih subjekata</li>\r\n<li>Priprema i podno&scaron;enje predloga za pokretanje stečajnog postupka, savetovanje klijenata o svim aspektima sporova povezanih sa stečajem, kao i o postupku reorganizacije</li>\r\n<li>Usklađivanje dokumenata sa novim zakonskim propisima</li>\r\n</ul>','<ul>\r\n<li>Advising small and medium &ndash; sized companies in solving potential problems in business operations</li>\r\n<li>Advising of corporations and concerns in respect of the business, corporate governance and other supporting activities</li>\r\n<li>Preparation of all types of contracts relevant to the operations of the company</li>\r\n<li>Monitoring of legislation and propose legislative solutions according to the needs of clients, as well as the overall business environment</li>\r\n<li>Joint ventures, restructuring, changes in status</li>\r\n<li>Creating due diligence reports</li>\r\n<li>Preparation documentations for shareholders assembly meetings, board of directors and supervisory boards meetings</li>\r\n<li>Consulting in the field of privatization of legal entities</li>\r\n<li>Advising clients on all aspects of disputes related to bankruptcy, as well as the reorganization</li>\r\n<li>Harmonization of documents with the new legislation</li>\r\n</ul>','2016-02-10 15:41:13',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (2,'Radno pravo','Labor Law','<ul>\r\n<li>Priprema op&scaron;tih i pojedinačnih pravnih akata za poslodavce i udruženja poslodavaca</li>\r\n<li>Savetovanje klijenata povodom dono&scaron;enja odluka u vezi sa zapo&scaron;ljavanjem i uslovima rada</li>\r\n<li>Izrada pravilnika, ugovora o radu i aneksa istih kao i sporazuma o prestanku radnog odnosa uključujući i ostalu potrebnu dokumentaciju</li>\r\n<li>Savetovanje zaposlenih prilikom zaključivanja ugovora o radu , kao i o pravima tokom rada</li>\r\n<li>Učestvovanje u pregovorima sa sindikatima u postupku zaključenja kolektivnog ugovora o radu</li>\r\n<li>Sporovi u vezi sa radnim odnosima, uključujući i sporove povodom kolektivnih ugovora</li>\r\n<li>Savetovanje povodom re&scaron;avanja vi&scaron;ka zaposlenih, kao i outrsourcing programa</li>\r\n<li>Pružanje pravnih saveta u pogledu svih aspekata zdravlja i bezbednosti na radu</li>\r\n<li>Priprema i izrada dokumentacije potrebne za pribavljanje radnih dozvola za strance</li>\r\n<li>Priprema akata potrebnih za upućivanje zaposlenih na rad u inostranstvu</li>\r\n<li>Zastupanje pred Arbitražom</li>\r\n</ul>','<ul>\r\n<li>Preparation of general and individual legal acts for employers and employers associations</li>\r\n<li>Advising clients regarding decision-making in relation to employment and working conditions</li>\r\n<li>Preparation of rulebooks, employment contracts and annexes thereof, as well as an agreements of termination of employment contracts including other necessary documentation</li>\r\n<li>Advising employees before concluding the labor contract, as well as advising of their legal rights during working hours</li>\r\n<li>Participation in negotiations with trade unions in the process of concluding of collective labor agreements</li>\r\n<li>Disputes with respect to labor issues, including disputes regarding collective agreements</li>\r\n<li>Advising clients in process of redundancy and outsourcing programs</li>\r\n<li>Providing legal advice on all aspects of health and safety at work</li>\r\n<li>Preparation of documents necessary for obtaining work permits for foreigners</li>\r\n<li>Preparation of acts necessary to make the employees to work abroad</li>\r\n<li>Representation in Arbitration</li>\r\n</ul>','2016-02-10 15:41:30',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (3,'Internet, pravo u oblasti oglašavanja i medijsko pravo','Internet, Advertising and Media Law','<ul>\r\n<li>Savetovanje u poslovanju kompanija u vezi sa elektronskom trgovinom, za&scaron;titi podataka o ličnosti, ogla&scaron;avanjem putem interneta i drugih medija</li>\r\n<li>Pravni saveti i procedure u vezi sa ogla&scaron;avanjem</li>\r\n<li>Pravni saveti u vezi sa postupcima za pribavljanje dozvola za obavljanje delatnosti u oblasti medija kao i zastupanje klijenata pred nadležnim organima</li>\r\n<li>Zastupanje u sporovima povodom objavljivanja informacija u medijima</li>\r\n<li>Savetovanje organizacija koje se bave mobilnom telefonijom, Internet i kablovskim uslugama</li>\r\n<li>Pružanje pravnih saveta i zastupanje u postupcima pred Republičkom radiodifuznom agencijom i drugim nadležnim organima</li>\r\n<li>Savetovanje i pomoć u oblasti za&scaron;tite intelektualne svojine iz oblasti informacionih tehnologija</li>\r\n</ul>','<ul>\r\n<li>Advising clients regarding e-commerce, the protection of personal data, advertising via the Internet and other media</li>\r\n<li>Legal advice and procedures related to advertising</li>\r\n<li>Legal advice in connection with procedures for obtaining a working license in the field of media as well as representing clients at the competent authorities</li>\r\n<li>Representation in disputes relating to disclosure of information in the media</li>\r\n<li>Advising legal entities engaged in mobile telephony, Internet and cable services business</li>\r\n<li>Providing legal advice and representation in proceedings at the RBA (Republic Broadcasting Agency) and other competent authorities</li>\r\n<li>Advice and assistance in the field of intellectual property protection in the field of information technology</li>\r\n</ul>','2016-02-10 15:41:50',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (4,'Intelektualna svojina','Intellectual Property','<ul>\r\n<li>Registracija žiga, patenta i industrijskog dizajna pred Zavodom za intelektualnu svojinu</li>\r\n<li>Izrada ugovora o licenci, fran&scaron;izingu, autorskih ugovora i ugovora o sponzorstvu</li>\r\n<li>Autorska prava</li>\r\n<li>Prava proizvođača fonograma i interpretatora</li>\r\n<li>Sprovođenje istraživanja o mogućnosti registracije žigova, patenata i industrijskog dizajna</li>\r\n<li>Pretraga naziva domena, njihova registracija i za&scaron;tita</li>\r\n<li>Zastupanje pred Zavodom za intelektualnu svojinu i Upravom carine</li>\r\n<li>Za&scaron;tita prava intelektulane svojine pred sudovima i arbitražama</li>\r\n</ul>','<ul>\r\n<li>Registration of trademark, patent and industrial design at the Intellectual Property Office</li>\r\n<li>Preparation of license, franchise, copyright and sponsorship agreements</li>\r\n<li>Copyrights protection</li>\r\n<li>The rights of producers of phonograms and interpreters</li>\r\n<li>Conducting research regarding registrations of trademarks, patents and industrial designs</li>\r\n<li>Search for domain names, their registration and protection</li>\r\n<li>Representation at the Intellectual Property Office and the Customs</li>\r\n<li>Protection of intellectual property rights at courts and arbitrations</li>\r\n</ul>','2016-02-10 15:42:07',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (5,'Nepokretnosti','Real estate','<ul>\r\n<li>Savetovanje i pružanje pomoći prilikom vođenja pregovora i zaključivanja predugovora i ugovora o kupoprodaji i ugovora o zakupu nepokretnosti</li>\r\n<li>Učestvovanje u izradi izmena i dopuna Zakona o planiranju i izgradnji i podzakonskih akata koji se tiču postupaka dobijanja lokacijskih uslova, građevinske dozvole, prijave radova i upotrebne dozvole u okviru objedinjene procedure</li>\r\n<li>Pružanje pomoći klijentima u procedurama za dobijanje dozvola, izgradnju, zoniranje, registraciju i drugim procedurama pred nadležnim organima</li>\r\n<li>Postupci pred nadležnim službama za Katastar nepokretnosti u vezi sa upisom prava svojine, uspostavljanjem, upisom, realizacijom i brisanjem hipoteke</li>\r\n<li>Pravno savetovanje u vezi sa urbanističkim uslovima i formalnostima u vezi sa pribavljanjem građevinskih i upotrebnih dozvola</li>\r\n<li>Legalizacija objekata i bespravno dograđenih zasebnih celina</li>\r\n<li>Pružanje pravnih saveta u vezi sa poreskim obavezama koje nastaju iz prometa nepokretnosti</li>\r\n<li>Promena namene zemlji&scaron;ta (komercijalno, industrijsko, poljoprivredno)</li>\r\n</ul>','<ul>\r\n<li>Advice and assistance in negotiation and conclusion of the preliminary and final sales contracts and lease of real estate contracts</li>\r\n<li>Participation in drafting amendments to the Law on Planning and Construction and regulations concerning the procedures for obtaining locational conditions, building permits, registration papers and use permits within a unified procedure</li>\r\n<li>Assisting clients in the procedures for obtaining permits, building, zoning, registration and other procedures at competent authorities</li>\r\n<li>Procedures at the competent real estate cadastral regarding registration of property rights, the establishment, registration, realization and deleting mortgages</li>\r\n<li>Legal advice in relation to urban conditions and formalities regarding obtaining construction and use permits</li>\r\n<li>Legalization of facilities and illegally annexed totalities</li>\r\n<li>Providing legal advice regarding tax liabilities arising from real estate transactions</li>\r\n<li>Change of use of land (commercial, industrial, agricultural)</li>\r\n</ul>','2016-02-10 15:42:22',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (6,'Pravo konkurencije i pravo u vezi sa zaštitom potrošača','Competition Law and Consumer Protection Law','<ul>\r\n<li>Pružanje pravne pomoći i savetovanje klijenata u vezi sa pitanjem konkurencije u različitim sporazumima, uključujući ugovore o licenciranju, distribuciji, fran&scaron;izingu, istraživanju i razvoju</li>\r\n<li>Zastupanje u sporovima u vezi sa zloupotrebom dominantnog položaja na trži&scaron;tu i antimonopolskim sporovima</li>\r\n<li>Zastupanje klijenata pred Komisijom za za&scaron;titu konkurencija u postupku dobijanja odobrenja koncentracije</li>\r\n<li>Pravni saveti o aspektima konkurencije u vezi sa spajanjima, pripajanjima i zajedničkim ulaganjima</li>\r\n<li>Podno&scaron;enje i priprema potrebne dokumentacije za dobijanje dozvole za spajanje od nadležnih tela za za&scaron;titu konkurencije u Srbiji</li>\r\n<li>Ocena usagla&scaron;enosti, uključujući ocenu usagla&scaron;enosti trgovinskih ugovora, savetovanje u trgovini i internoj politici cena, kao i kontrola transakcija sa aspekta prava konkurencije</li>\r\n<li>Program usagla&scaron;avanja; kontrola i ostalog tekućeg poslovanja ( ugovora sa ostalim konkurentnim privrednim subjektima, članstva u trgovinskim asocijacijama, odnosima sa kupcima, trgovinskim ugovorima), procena rizika i pomoć u usagla&scaron;avanju interne politike klijenta u cilju izbegavanja eventualnog budućeg prekr&scaron;aja</li>\r\n<li>Savetovanje trgovaca i proizvođača u vezi sa materijom koju uređuje Zakon o za&scaron;titi potro&scaron;ača</li>\r\n<li>Savetovanje i za&scaron;tita prava potro&scaron;ača</li>\r\n</ul>','<ul>\r\n<li>Providing legal assistance and counseling clients regarding the issue of competition in the various agreements, including agreements on licensing, distribution, franchising, research and development</li>\r\n<li>Representation in disputes relating to the abuse of a dominant position in the market and antitrust disputes</li>\r\n<li>Representing clients at the Commission for Protection of Competition in the process of obtaining approval of concentration</li>\r\n<li>Legal advice on aspects of competition in connection with mergers, acquisitions and joint ventures</li>\r\n<li>Submission and preparation of necessary documents for obtaining mergers permission from the competent body for protection of competition in Serbia</li>\r\n<li>Conformity assessment, including assessment of compliance of trade agreements, trade counseling and internal pricing policy, as well as the monitoring of transactions from the aspect of competition law</li>\r\n<li>Program reconciliation; controls and other current operations (agreements with other competitive businesses, membership in trade associations, customer relations, trade agreements), risk assessment and help in streamlining internal politics client in order to avoid possible future violations</li>\r\n<li>Advising traders and manufacturers regarding the area that regulates the Consumer Protection Act</li>\r\n<li>Advice and protection of consumer rights</li>\r\n</ul>','2016-02-10 15:42:45',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (7,'Ugovori i naknada štete','Contracts and compensation','<ul>\r\n<li>Sastavljanje svih vrsta poslovnih ugovora i učestvovanje u pregovorima</li>\r\n<li>Savetovanje klijenta u vezi sa postupcima naknade svih vidova &scaron;tete</li>\r\n</ul>','<ul>\r\n<li>The preparation of all types of commercial contracts and participation in negotiations</li>\r\n<li>Advising clients regarding all forms of compensations for damage</li>\r\n</ul>','2016-02-10 15:43:08',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (8,'Krivično pravo','Criminal Law','<ul>\r\n<li>Prethodno savetovanje o strategiji odbrane klijenata u svim fazama krivičnog postupka</li>\r\n<li>Zastupanje klijenata (fizičkih i pravnih lica) u krivičnom delima i privrednim prestupima</li>\r\n<li>Izrada žalbe na odluku suda kao i druge neophodne dokumentacije</li>\r\n<li>Pravna pomoć po osnovu izvr&scaron;enje krivične sankcije &ndash; kazne</li>\r\n</ul>','<ul>\r\n<li>Preliminary consultation on the strategy of the defense of clients in all stages of criminal proceedings</li>\r\n<li>Representing clients (civil and legal entities) in criminal acts</li>\r\n<li>Making an appeal in criminal proceedings and other necessary documentation</li>\r\n<li>Legal assistance regarding the execution of criminal penalties &ndash; fines</li>\r\n</ul>','2016-02-10 15:43:28',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (9,'Sporovi','Disputes','<ul>\r\n<li>Zastupanje klijenata u privrednim sporovima, upravnim, izvr&scaron;nim i drugim postupcima</li>\r\n<li>Analiza rizika vođenja postupka</li>\r\n<li>Izrada strategije postupka</li>\r\n<li>Priprema pravnih izve&scaron;taja</li>\r\n<li>Prikupljanje dokaza, pripremanje tužbenih zahteva, žalbi, zahteva i drugih akata</li>\r\n<li>Pripremanje nacrta i sprovođenje sporazuma o poravnanju</li>\r\n<li>Arbitraža</li>\r\n</ul>','<ul>\r\n<li>Representing clients in commercial disputes, administrative, executive and other procedures</li>\r\n<li>Risk analysis of procedures</li>\r\n<li>Development of procedure strategy</li>\r\n<li>Preparation of legal reports</li>\r\n<li>The collection of evidence, preparing damage claims, appeals, requests and other documents</li>\r\n<li>Preparation and implementation of settlement agreements</li>\r\n<li>Arbitration</li>\r\n</ul>','2016-02-10 15:43:42',1,0);
INSERT INTO `services` (`id`,`title_sr`,`title_en`,`body_sr`,`body_en`,`date_created`,`published`,`order`) VALUES (10,'Registracija – osnivanje firmi i izrada dokumentacije','Registration of companies and documentation','<ul>\r\n<li>Osnivanje svih pravnih formi privrednih dru&scaron;tava i drugih pravnih lica</li>\r\n<li>Registracija i postupci pred Agencijom za privredne registre i drugim organima i ustanovama</li>\r\n<li>Konsultacije, pregovori i izrada osnivačkih akata, statuta, ugovora članova dru&scaron;tva, pravilnika o internim procedurama i drugih internih dokumenata</li>\r\n<li>Usklađivanje dokumenata sa novim zakonskim propisima</li>\r\n</ul>','<ul>\r\n<li>Establishment of all legal forms of companies and other legal entities</li>\r\n<li>Registration and proceedings at the Business Registers Agency and other authorities and institutions</li>\r\n<li>Preparation of company founding acts, statutes, rulebooks, internal procedures rules and other internal documents</li>\r\n<li>Harmonization of documents with the new legislation</li>\r\n</ul>','2016-02-10 15:44:02',1,0);
